n<query>\n<!-- Drilldown SQL Query here -->\nSELECT \n*\nFROM demographic d\nLEFT JOIN ( \nSELECT dxresearch_no, demographic_no\nFROM dxresearch\nWHERE demographic_no>0\nAND status = \'A\'\nAND coding_system = \'icd9\'\nAND dxresearch_code IN ${dxexclude}\nGROUP BY demographic_no HAVING COUNT(demographic_no) > -1\n\n)dxr \nON ( d.demographic_no = dxr.demographic_no) \n\n\n-- GET last NOT refused MAMMOGRAM ENTRIES FROM PREVENTIONS\n                        LEFT JOIN (\n                                SELECT p.demographic_no, MAX(p.prevention_date) AS prevention_date, pe.val, p.id, IF(p.refused=0, "Complete", "Refused") AS refused\n                                FROM preventions p\nLEFT JOIN preventionsExt pe\n                                ON ( pe.prevention_id = p.id ) \n                                WHERE p.prevention_type = ${prevention}\n                                AND p.deleted = 0\n                                AND p.refused = 0\nAND pe.keyval = "result" \nAND pe.val != "pending"\n                                GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                        ) MAM\n                        ON (d.demographic_no = MAM.demographic_no)\n\n-- latest tracking status\n                        LEFT JOIN (\n\n                                SELECT p.demographic_no, p.id, pe.val, p.prevention_date, IF(p.refused = 1, "Refused", IF(p.refused = 2, "Ineligible", "Complete") ) AS refused\n                                FROM preventions p\n                                JOIN preventionsExt pe\n                                ON p.id = pe.prevention_id\n                                RIGHT JOIN (\n                                        SELECT demographic_no, MAX(prevention_date) AS prevention_date\n                                        FROM preventions\n                                        WHERE prevention_type = ${prevention}\n                                        AND deleted = 0\n                                        GROUP BY demographic_no\n                                ) p2\n                                ON p.prevention_date = p2.prevention_date\n                                AND p.demographic_no = p2.demographic_no\n                                AND p.prevention_type = ${prevention}\nAND p.deleted = 0\n                                WHERE pe.keyval = "result"\n                                ORDER BY p.id DESC\n                        ) MAM0\n                        ON (d.demographic_no = MAM0.demographic_no)\n\n                        -- MAM pending\n                        LEFT JOIN (\n                                SELECT p.demographic_no, p.id, MAX(prevention_date) AS prevention_date\n                                FROM preventions p\n                                LEFT JOIN preventionsExt pe\n                                ON ( pe.prevention_id = p.id ) \n                                WHERE p.prevention_type = ${prevention}\n                                AND p.deleted = 0\n                                AND p.refused = 0\nAND pe.keyval = "result" \nAND pe.val = "pending"\nAND DATE(p.prevention_date) >= ${lowerLimit.date24}\n                                GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                        ) MAM_p\n                        ON (d.demographic_no = MAM_p.demographic_no)\n\n                        -- MAM declined or refused\n                        LEFT JOIN (\n                                SELECT p.demographic_no, p.id\n                                FROM preventions p\n                                WHERE p.prevention_type = ${prevention}\n                                AND p.deleted = 0\n                                AND p.refused = 1\nAND DATE(p.prevention_date) >= ${lowerLimit.date24}\n                                GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                        ) MAM_d\n                        ON (d.demographic_no = MAM_d.demographic_no)\n\n-- Last Seen Date\nLEFT JOIN (\n                                SELECT a.demographic_no, max(a.appointment_date) as appointment_date\n                                FROM appointment a, demographic d\n                                WHERE a.demographic_no = d.demographic_no\n                                AND d.provider_no = \'${provider}\'\n                                AND ${upperLimit.date} > DATE(a.appointment_date)\n                                AND a.status != \'D\' AND a.status != \'C\' AND a.status != \'N\'\n                                AND a.demographic_no > 0\n                                GROUP BY a.demographic_no\n                        ) app1\n                        ON (d.demographic_no = app1.demographic_no)\n\n                        -- Next Appointment Date\nLEFT JOIN (\n                                SELECT a.demographic_no, a.appointment_date\n                                FROM appointment a, demographic d\n                                WHERE a.demographic_no = d.demographic_no\n                                AND d.provider_no = \'${provider}\'\n                                AND DATE(a.appointment_date) >= ${upperLimit.date}\n                                AND a.status != \'D\' AND a.status != \'C\' AND a.status != \'N\'\n                                AND a.demographic_no > 0\n                                GROUP BY a.demographic_no HAVING COUNT(a.demographic_no) > -1\n                        ) app2\n                        ON (d.demographic_no = app2.demographic_no)\n\n-- billing for Q131A\n                        LEFT JOIN (\n                                SELECT boc.demographic_no, MAX(boi.service_date) as service_date, boi.id\n                                FROM billing_on_cheader1 boc, billing_on_item boi\n                                WHERE boc.id = boi.ch1_id\nAND boc.status != \'D\'\n                                AND boi.status != \'D\'\n                                AND boi.service_code =  ${servicecode131}\n                                GROUP BY boc.demographic_no\n                        ) BILL131\n                        ON (d.demographic_no = BILL131.demographic_no)\n\n                        -- WITHOUT a service code of Q141A billed\n                        LEFT JOIN (\n                                SELECT boc.id, boc.demographic_no, boc.billing_date\n                                FROM billing_on_cheader1 boc, billing_on_item boi\n                                WHERE boc.id = boi.ch1_id\nAND boc.status != \'D\'\n                                AND boi.status != \'D\'\n                                AND boi.service_code = ${servicecode141}\n                                GROUP BY boc.demographic_no\n                        ) BILL141\n                        ON (d.demographic_no = BILL141.demographic_no)\n\n-- Notes\n                        -- Use Ineligible in 24month searching to replace note searching\n                        LEFT JOIN (\n\n                                SELECT demographic_no, id\n                                FROM preventions\n                                WHERE prevention_type = ${prevention}\n                                AND deleted = 0\n                                AND refused = 2\n                                AND prevention_date >= ${lowerLimit.date24}\n                                GROUP BY demographic_no\n\n                        ) MAM_i\n                        ON (d.demographic_no = MAM_i.demographic_no)\n\nWHERE d.patient_status = ${pstatus}\nAND d.provider_no = \'${provider}\'\nAND d.sex = ${gender}\nAND d.demographic_no > 0 \nAND FLOOR( ABS( DATEDIFF( DATE( CONCAT(d.year_of_birth,"-",d.month_of_birth,"-",d.date_of_birth) ), ${upperLimit.date} ) / 365.25 ) ) \nBETWEEN ${lowerLimit.age} AND ${upperLimit.age}\nAND dxr.dxresearch_no IS NULL\nAND MAM_i.id IS NULL\nAND BILL141.id IS NULL\nGROUP BY d.demographic_no HAVING COUNT(d.demographic_no) > -1\n</query>\n</drillDownQuery>\n<shared>true</shared>\n<sharedMetricSetName>OntarioMD Breast Cancer Screening Overdue</sharedMetricSetName>\n<sharedMetricDataId>Status</sharedMetricDataId>\n<sharedMappings>\n                <sharedMapping fromLabel="% less than 3 months overdue" toLabel="&lt;= 3 months Overdue"/>\n                <sharedMapping fromLabel="% more than 3 months overdue" toLabel="&gt; 3 months Overdue"/>\n                <sharedMapping fromLabel="% less than 2 months pending" toLabel="&lt; 2 months Pending"/>\n                <sharedMapping fromLabel="% more than 2 months pending" toLabel="&gt;= 2 months Pending"/>\n                <sharedMapping fromLabel="% Declined" toLabel="Declined"/>\n                <sharedMapping fromLabel="% Due in less than 4 months" toLabel="Due in &lt; 4 months"/>\n                <sharedMapping fromLabel="% Due in more than 4 and less than 7 months" toLabel="Due in &gt;= 4 and &lt; 7 months"/>\n        </sharedMappings>\n</indicatorTemplateXML>\n', b'1', b'0', 1, '', 'OntarioMD Breast Cancer Screening Overdue');
        INSERT INTO `indicatorTemplate` (`dashboardId`, `name`, `category`, `subCategory`, `framework`, `frameworkVersion`, `definition`, `notes`, `template`, `active`, `locked`, `shared`, `metricLabel`, `metricSetName`) VALUES
        (@dashboardId3, 'Colorectal Cancer Screening Recall', 'Preventive Health Care', 'Cancer', 'OSCAR EMR', '2022-02-02', 'Patient Care: Colorectal Cancer Screening:\n % of patients aged 50 to 74 years old, inclusive, with an FOBT  within the past two years, \n sigmoidoscopy within 5 years or a colonoscopy within the past 10 years. - patients with a null or pending preven', 'ON', '<?xml version="1.0" encoding="UTF-8"?>\n<indicatorTemplateXML>\n<author>Trinity Healthcare Technologies for Oscar EMR, bugs by PHC</author>\n<uid></uid>\n<heading>\n<category>Preventive Health Care (Click on the chart to see details)</category>\n<subCategory>Cancer</subCategory>\n<name>Colorectal Cancer Screening Recall</name>\n<metricSetName>Colorectal Cancer Screen Overdue</metricSetName>\n<metricLabel></metricLabel>\n<definition>Patient Care: Colorectal Cancer Screening:\n Patients aged 50 to 74 years old, inclusive, with an FOBT  within the past two years, \n sigmoidoscopy within 5 years or a colonoscopy within the past 10 years. - patients with a null or pending prevention result are overdue.</definition>\n<framework>Based on and adapted from CIHIs 2012 Indicator Technical Specifications (Nov 2012)</framework>\n<frameworkVersion>2022-03-03</frameworkVersion>\n<notes>ON</notes><graphType>table</graphType>\n</heading>\n<indicatorQuery>\n<version>2022-03-03</version>\n<params>\n<!-- \nRequired parameter provider. Value options are: \n[ an individual providerNo, or provider range ] ie: value="370, 1001" \n"all" ie: value="all" including null.\n"loggedInProvider" ie:\nDefault is "loggedInProvider"\nUse this parameter in the query as ${provider}\nThis parameter should be used for fetching patient\'s assigned to a MRP.\nie: WHERE demographic.provider_no = ${provider}\n-->\n<parameter id="provider" name="provider_no" value="loggedInProvider" />\n<parameter id="pstatus" name="Patient Status" value="\'AC\'" />\n<parameter id="loincfobt" name="FOBT LOINC" value="2335-8,29771-3,58453-2,14563-1,14564-9,14565-6" />\n<parameter id="colonoscopy" name="Colonoscopy" value="\'COLONOSCOPY\'" />\n<parameter id="dxexclusions" name="Exclude Dx Codes" value="154,153,556,555,V1005" />\n<parameter id="preventionfobt" name="FOBT Prevention" value="\'FOBT\'" />\n\n<parameter id="servicecode" name="Service Code Prevention" value="\'Q133A\'" />\n</params>\n<range>\n<upperLimit id="age" label="Max Age" name="Age" value="74" />\n<lowerLimit id="age" label="Min Age" name="Age" value="50" />\n\n<upperLimit id="date" label="Current Date" name="currentDate" value="NOW()" />\n\n<lowerLimit id="date2" label="2 Months" name="Date2" value="DATE_SUB( NOW(), INTERVAL 2 MONTH )" />\n<lowerLimit id="date3" label="3 Months" name="Date3" value="DATE_SUB( NOW(), INTERVAL 3 MONTH )" />\n\n<lowerLimit id="date16" label="16 Months" name="Date16" value="DATE_SUB( NOW(), INTERVAL 16 MONTH )" />\n<lowerLimit id="date17" label="17 Months" name="Date17" value="DATE_SUB( NOW(), INTERVAL 17 MONTH )" />\n<lowerLimit id="date20" label="20 Months" name="Date20" value="DATE_SUB( NOW(), INTERVAL 20 MONTH )" />\n<lowerLimit id="date21" label="21 Months" name="Date21" value="DATE_SUB( NOW(), INTERVAL 21 MONTH )" />\n<lowerLimit id="date24" label="24 Months" name="Date24" value="DATE_SUB( NOW(), INTERVAL 24 MONTH )" />\n<lowerLimit id="date112" label="112 Months" name="Date112" value="DATE_SUB( NOW(), INTERVAL 112 MONTH )" />\n<lowerLimit id="date113" label="113 Months" name="Date113" value="DATE_SUB( NOW(), INTERVAL 113 MONTH )" />\n<lowerLimit id="date116" label="116 Months" name="Date116" value="DATE_SUB( NOW(), INTERVAL 116 MONTH )" />\n<lowerLimit id="date117" label="117 Months" name="Date117" value="DATE_SUB( NOW(), INTERVAL 117 MONTH )" />\n<lowerLimit id="date120" label="120 Months" name="Date120" value="DATE_SUB( NOW(), INTERVAL 120 MONTH )" />\n\n<lowerLimit id="date27" label="27 Months" name="Date27" value="DATE_SUB( NOW(), INTERVAL 27 MONTH )" />\n<lowerLimit id="date25" label="25 Months" name="Date25" value="DATE_SUB( NOW(), INTERVAL 25 MONTH )" />\n\n<lowerLimit id="date121" label="121 Months" name="Date121" value="DATE_SUB( NOW(), INTERVAL 121 MONTH )" />\n<lowerLimit id="date123" label="123 Months" name="Date123" value="DATE_SUB( NOW(), INTERVAL 123 MONTH )" />\n<lowerLimit id="date124" label="124 Months" name="Date123" value="DATE_SUB( NOW(), INTERVAL 124 MONTH )" />\n</range>\n<query>\n<!-- Indicator SQL Query here -->\nSELECT \nIF ( COUNT(fin.patient) > 0,\n SUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt24 = 0 AND fin.colo120 = 0 AND fin.bill24 = 0 AND fin.fobt_d24 = 0 AND fin.fobt_p24 = 0 AND (fin.fobt27_25 > 0 OR fin.colo123_121 > 0 OR fin.bill27_25 > 0) , 1, 0 ))\n,0 ) AS \'&lt;= 3 months Overdue\',\n\nIF ( COUNT(fin.patient) > 0,\n SUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt27 = 0 AND fin.colo123 = 0 AND fin.bill27 = 0 AND fin.fobt_d24 = 0 AND fin.fobt_p24 = 0, 1, 0 ))\n,0 ) AS \'&gt; 3 months Overdue\', \n\nIF ( COUNT(fin.patient) > 0,\nSUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt24 = 0 AND fin.colo120 = 0 AND fin.bill24 = 0 AND fin.fobt_p2 > 0, 1, 0 ))\n,0 ) AS \'&lt; 2 months Pending\',\n\nIF ( COUNT(fin.patient) > 0,\nSUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt24 = 0 AND fin.colo120 = 0 AND fin.bill24 = 0 AND fin.fobt_p2 = 0 AND fin.fobt_p24_3 > 0, 1, 0 ) )\n,0 ) AS \'&gt;= 2 months Pending\',\n\nIF ( COUNT(fin.patient) > 0,\nSUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt24 = 0 AND fin.colo120 = 0 AND fin.bill24 = 0 AND fin.fobt_p24 = 0 AND fin.fobt_d24 > 0, 1, 0 ) )\n,0 ) AS \'Declined\',\n\nIF ( COUNT(fin.patient) > 0,\n SUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt20 = 0 AND fin.colo116 = 0 AND fin.bill20 = 0 AND ( fin.fobt24_21 > 0 OR fin.colo120_117 > 0 OR fin.bill24_21 > 0 ), 1, 0 ) )\n,0 ) AS \'Due in &lt; 4 months\',\n\nIF ( COUNT(fin.patient) > 0,\n SUM( IF( fin.dx = 0 AND fin.fobt_i = 0 AND fin.fobt16 = 0 AND fin.colo112 = 0 AND fin.bill16 = 0 AND ( fin.fobt20_17 > 0 OR fin.colo116_113 > 0 OR fin.bill20_17 > 0 ), 1, 0 ) )\n,0 ) AS \'Due in &gt;= 4 and &lt; 7 months\'\nFROM (\n\nSELECT \n    d.demographic_no AS patient,\n    IFNULL(COLO112.id, 0) AS colo112,\n    IFNULL(COLO116.id, 0) AS colo116,\n    IFNULL(COLO120.id, 0) AS colo120,\n    IFNULL(COLO123.id, 0) AS colo123,\n    IFNULL(COLO123_121.id, 0) AS colo123_121,\n    IFNULL(COLO124.id, 0) AS colo124,\n    IFNULL(COLO120_117.id, 0) AS colo120_117,\n    IFNULL(COLO116_113.id, 0) AS colo116_113,\n    IFNULL(FOBT24.id, 0) AS fobt24,\n    IFNULL(FOBT27_25.id, 0) AS fobt27_25,\n    IFNULL(FOBT27.id, 0) AS fobt27,\n    IFNULL(FOBT24_21.id, 0) AS fobt24_21,\n    IFNULL(FOBT20_17.id, 0) AS fobt20_17,\n    IFNULL(FOBT20.id, 0) AS fobt20,\n    IFNULL(FOBT16.id, 0) AS fobt16,\n    IFNULL(FOBT_p2.id, 0) AS fobt_p2,\n    IFNULL(FOBT_p24.id, 0) AS fobt_p24,\n    IFNULL(FOBT_p24_3.id, 0) AS fobt_p24_3,\n    IFNULL(FOBT_d24.id, 0) AS fobt_d24,\n    IFNULL(BILL16.id, 0) AS bill16,\n    IFNULL(BILL20.id, 0) AS bill20,\n    IFNULL(BILL24.id, 0) AS bill24,\n    IFNULL(BILL27_25.id, 0) AS bill27_25,\n    IFNULL(BILL27.id, 0) AS bill27,\n    IFNULL(BILL24_21.id, 0) AS bill24_21,\n    IFNULL(BILL20_17.id, 0) AS bill20_17,\n    IFNULL(FOBT_i.id, 0) AS fobt_i,\n    IFNULL(dxr.dxresearch_no, 0) AS dx\n\nFROM demographic d\n\nLEFT JOIN (\n            SELECT dxresearch_no, demographic_no\n            FROM dxresearch\n            WHERE demographic_no>0\n            AND dxresearch_code IN ${dxexclusions}\n            AND `status` = \'A\'\n            AND coding_system = \'icd9\'\n            GROUP BY demographic_no HAVING COUNT(demographic_no) > -1\n            ) dxr \n            ON ( d.demographic_no = dxr.demographic_no)\n\nLEFT JOIN (\n                                        SELECT p.demographic_no, p.id, pe.val\n                                        FROM preventions p\n                                        INNER JOIN preventionsExt pe\n                                        ON (pe.prevention_id = p.id AND pe.keyval = "result")\n                                        WHERE p.prevention_type = ${colonoscopy}\n                                        AND p.deleted = 0\n                                        AND p.refused = 0\n                                        AND pe.val != "pending"\n                                        AND DATE(p.prevention_date) > ${lowerLimit.date113}\n                                        GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                                ) COLO112\n                                ON (d.demographic_no = COLO112.demographic_no )\n\n\nLEFT JOIN (\n                                        SELECT p.demographic_no, p.id, pe.val\n                                        FROM preventions p\n                                        INNER JOIN preventionsExt pe\n                                        ON (pe.prevention_id = p.id AND pe.keyval = "result")\n                                        WHERE p.prevention_type = ${colonoscopy}\n                                        AND p.deleted = 0\n                                        AND p.refused = 0\n                                        AND pe.val != "pending"\n                                        AND DATE(p.prevention_date) >= ${lowerLimit.date116}\n                                        GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                                ) COLO116\n                                ON (d.demographic_no = COLO116.demographic_no )\n\n\n-- Look for colonoscopy results from the last 120 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${colonoscopy}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date120}\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) COLO120\n            ON (d.demographic_no = COLO120.demographic_no )\n\nLEFT JOIN (\n                                        SELECT p.demographic_no, p.id, pe.val\n                                        FROM preventions p\n                                        INNER JOIN preventionsExt pe\n                                        ON (pe.prevention_id = p.id AND pe.keyval = "result")\n                                        WHERE p.prevention_type = ${colonoscopy}\n                                        AND p.deleted = 0\n                                        AND p.refused = 0\n                                        AND pe.val != "pending"\n                                        AND DATE(p.prevention_date) >= ${lowerLimit.date123}\n                                        GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                                ) COLO123\n                                ON (d.demographic_no = COLO123.demographic_no )\n\n-- Look for colonoscopy results Between 121 -123 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${colonoscopy}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date123} AND ${lowerLimit.date120} > DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) COLO123_121\n            ON (d.demographic_no = COLO123_121.demographic_no )\n\n-- Look for colonoscopy results from the last 124 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${colonoscopy}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date124}\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) COLO124\n            ON (d.demographic_no = COLO124.demographic_no )\n\n-- Look for colonoscopy results Between 117 -120 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${colonoscopy}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date120} AND ${lowerLimit.date116} > DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) COLO120_117\n            ON (d.demographic_no = COLO120_117.demographic_no )\n\n-- Look for colonoscopy results Between 113 -116 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${colonoscopy}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date116} AND ${lowerLimit.date113} >= DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) COLO116_113\n            ON (d.demographic_no = COLO116_113.demographic_no )\n\n-- Look for FOBT req from the last 24 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date24}\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) FOBT24\n            ON (d.demographic_no = FOBT24.demographic_no )\n\n-- Look for FOBT req Between 25 -27 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date27} AND ${lowerLimit.date24} > DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) FOBT27_25\n            ON (d.demographic_no = FOBT27_25.demographic_no )\n\n-- Look for FOBT req from the last 27 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date27}\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) FOBT27\n            ON (d.demographic_no = FOBT27.demographic_no )\n\nLEFT JOIN (\n                                        SELECT p.demographic_no, p.id, pe.val\n                                        FROM preventions p\n                                        INNER JOIN preventionsExt pe\n                                        ON (pe.prevention_id = p.id AND pe.keyval = "result")\n                                        WHERE p.prevention_type = ${preventionfobt}\n                                        AND p.deleted = 0\n                                        AND p.refused = 0\n                                        AND pe.val != "pending"\n                                        AND DATE(p.prevention_date) >= ${lowerLimit.date20}\n                                        GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                                ) FOBT20\n                                ON (d.demographic_no = FOBT20.demographic_no )\n\nLEFT JOIN (\n                                        SELECT p.demographic_no, p.id, pe.val\n                                        FROM preventions p\n                                        INNER JOIN preventionsExt pe\n                                        ON (pe.prevention_id = p.id AND pe.keyval = "result")\n                                        WHERE p.prevention_type = ${preventionfobt}\n                                        AND p.deleted = 0\n                                        AND p.refused = 0\n                                        AND pe.val != "pending"\n                                        AND DATE(p.prevention_date) > ${lowerLimit.date17}\n                                        GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n                                ) FOBT16\n                                ON (d.demographic_no = FOBT16.demographic_no )\n\n-- Look for FOBT req Between 21 -24 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date24} AND ${lowerLimit.date20} > DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n            ) FOBT24_21\n            ON (d.demographic_no = FOBT24_21.demographic_no )\n\n-- Look for FOBT req Between 17 -20 months\nLEFT JOIN ( \n            SELECT p.demographic_no, p.id, pe.val\n            FROM preventions p\n            INNER JOIN preventionsExt pe\n            ON (pe.prevention_id = p.id AND pe.keyval = "result")\n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.val != "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date20} AND ${lowerLimit.date17} >= DATE(p.prevention_date)\n            GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n        ) FOBT20_17\n        ON (d.demographic_no = FOBT20_17.demographic_no )\n\n-- FOBT pending\nLEFT JOIN (\n           SELECT p.demographic_no, p.id, MAX(prevention_date) AS prevention_date\n           FROM preventions p\n           LEFT JOIN preventionsExt pe\n           ON pe.prevention_id = p.id\n           WHERE p.prevention_type = ${preventionfobt}\n           AND p.deleted = 0\n           AND p.refused = 0\n           AND pe.keyval = "result"\n            AND pe.val = "pending"\n            AND DATE(p.prevention_date) >= ${lowerLimit.date24} AND ${lowerLimit.date2} > DATE(p.prevention_date)\n           GROUP BY p.demographic_no HAVING COUNT(p.demographic_no) > -1\n           ) FOBT_p24_3\n                                ON (d.demographic_no = FOBT_p24_3.demographic_no)\n\nLEFT JOIN    (\n            SELECT p.demographic_no, p.id, MAX(prevention_date) AS prevention_date\n            FROM preventions p\n            LEFT JOIN preventionsExt pe\n            ON pe.prevention_id = p.id \n            WHERE p.prevention_type = ${preventionfobt}\n            AND p.deleted = 0\n            AND p.refused = 0\n            AND pe.